mapper.select:
select distinct bt.id,bt.type_name,count(ba.type_id) type_count from blog_type bt left join blog_articles ba on bt.id = ba.type_id
where bt.type_name LIKE '%${queryPageVO.queryString}%'
GROUP BY bt.id ORDER BY COUNT(ba.type_id) DESC LIMIT ${queryPageVO.pageSize * (queryPageVO.currentPage-1)},#{queryPageVO.pageSize}
